                var totalVal = 0;
                var taxAppliedValue = 0;
                // add checked cost centre values to gross total
                for (var i = 0; i < updatedAddOns().length; i++) {
                    jsonToReSubmit.push(updatedAddOns()[i]);
                }

                //displayTotalPrice(itemPrice, totalVal);
